// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title LuckyMilitiaMatchRegistry
 * @dev Records player sign-ins before matches on Base
 */
contract LuckyMilitiaMatchRegistry {
    mapping(address => uint256) public signInTime;
    
    event PlayerSignedIn(address indexed player, uint256 timestamp);
    
    /**
     * @dev Players call this to sign in before a match
     */
    function signIn() external {
        signInTime[msg.sender] = block.timestamp;
        emit PlayerSignedIn(msg.sender, block.timestamp);
    }
    
    /**
     * @dev Check when a player last signed in
     */
    function getSignInTime(address player) external view returns (uint256) {
        return signInTime[player];
    }
    
    /**
     * @dev Check if player has ever signed in
     */
    function hasSignedIn(address player) external view returns (bool) {
        return signInTime[player] > 0;
    }
}
